<?phpif (is_post_type_archive('galeria')){    $thumbnail = get_option('polivoz_template_galeria_header');    if(!empty($thumbnail)){echo ('<div class="parallax-window" data-image="'.$thumbnail.'"></div>');}    else{echo '<div class="space"></div>';}}elseif (is_post_type_archive('local')){    $thumbnail = get_option('polivoz_template_local_header');    if(!empty($thumbnail)){echo ('<div class="parallax-window" data-image="'.$thumbnail.'"></div>');}    else{echo '<div class="space"></div>';}}elseif (is_post_type_archive('evento')){    $thumbnail = get_option('polivoz_template_evento_header');    if(!empty($thumbnail)){echo ('<div class="parallax-window" data-image="'.$thumbnail.'"></div>');}    else{echo '<div class="space"></div>';}}elseif (is_post_type_archive('musica')){    $thumbnail = get_option('polivoz_template_musica_header');    if(!empty($thumbnail)){echo ('<div class="parallax-window" data-image="'.$thumbnail.'"></div>');}    else{echo '<div class="space"></div>';}}elseif (has_post_thumbnail( $post->ID ) ){    $thumbnail = wp_get_attachment_image_src( get_post_thumbnail_id($post->ID), 'slider-size' );    echo ('<div class="parallax-window" data-image="'.$thumbnail[0].'"></div>');}else{echo '<div class="space"></div>';}?><!--</header>--><h1 id="inicio"></h1>